import React from 'react';
import { View, Text, TouchableOpacity, Dimensions, StyleSheet } from 'react-native';

const { width, height } = Dimensions.get('window');

const Header = ({ userName }) => {
  return (
    <View style={styles.header}>
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.button}>
          {/* Conteúdo do botão */}
        </TouchableOpacity>
      </View>
      <Text style={styles.welcomeText}>Seja bem-vindo, {userName}!</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between', // Ajusta o texto à direita
    backgroundColor: '#1D65EB', // Cor de fundo do cabeçalho
    height: 60, // Altura do cabeçalho
  },
  buttonContainer: {
    flex: 1, // Ocupará o restante do espaço horizontal disponível
    justifyContent: 'center', // Centraliza horizontalmente o conteúdo
  },
  button: {
    width: width * 0.08, // 10% da largura da tela
    height: width * 0.08, // Mesma altura para manter o botão redondo
    borderRadius: width * 0.05, // Raio para torná-lo redondo
    backgroundColor: 'white', // Cor de fundo do botão redondo
    justifyContent: 'center',
    alignItems: 'center',
    margin: 5,
  },
  welcomeText: {
    flex: 2, // Ocupará mais espaço para acomodar o texto
    fontSize: 18,
    color: 'white', // Cor do texto
    marginLeft: 10, // Espaçamento à esquerda entre o botão e o texto
  },
});

export default Header;
