import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import Voltar from '../../components/Voltar';
import { useAuth } from '../LoginScreen/AuthContext'
import axios from 'axios'

const Cartao = () => {

  const [cartao, setCartao] = useState("");
  const [cep, setCep] = useState("");
  const { codigoLogado } = useAuth();

  useEffect(() => {
    async function fetchData() {
      try { //Get User
        const response = await axios.get(
          `http://10.109.71.22:8000/api/v1/cliente/${codigoLogado}/`,
          {
            headers: {
              Authorization: "Token 63d15c6d3adeb0eff6f27a2acaa9bc025f976c11",
            },
          }
        );
        setCartao(response.data.cartao);
        setCep(response.data.cep);
        
      }
      catch (error) {
        console.error("Erro ao obter usuário:", error);
      }

    try { //Post card
      const resposta = await axios.post(
        `http://10.109.71.22:8000/api/v1/cartao/${codigoLogado}/`,
        {
          codigo: codigoNumero,
          usuario: usuario,
          senha: senhaNumero,
        },
        {
          headers: {
            Authorization: `Token 63d15c6d3adeb0eff6f27a2acaa9bc025f976c11`,
          },
        }
      );

      Swal.fire({
        title: 'Login realizado',
        text: `Bem vindo ${usuario}`,
        icon: 'success',
      });
      const codigoLogado = codigoNumero;
      setCodigoLogado(codigoLogado); // Armazene no contexto
      handleLogin();
    } catch (erro) {
      // Lidar com erros aqui
      console.error('Erro ao enviar requisição:', erro);
    }
    }

    fetchData();
  }, []);

  return (
    
    <View  style={styles.all}> 
    <Voltar text="Cartão"/>
    <View style={styles.container}>

    
      {cartao === false ? (
        <Text style={styles.text}>Você ainda não possui cartão de crédito</Text>
      ):<Text>Seu Cartão ja foi solicitado, chegará no Cep: {cep}</Text>}
        
      <TouchableOpacity style={styles.button} >
        <Text style={styles.buttonText}>Pedir Cartão!</Text>
      </TouchableOpacity>
    </View>
    </View>
    
  );
};

const styles = StyleSheet.create({
    all: {
        flex: 1
    },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    fontSize: 20,
    textAlign: 'center',
    marginBottom: 20,
  },
  button: {
    backgroundColor: 'blue',
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 8,
  },
  buttonText: {
    color: 'white',
    fontSize: 18,
    textAlign: 'center',
  },
});

export default Cartao;
