import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, TouchableOpacity, Text } from 'react-native';
import Voltar from "../../components/Voltar";
import {useNavigation} from '@react-navigation/native'


const Cadastro = () => {
  const { navigate } = useNavigation();
  const [dados, setDados] = useState({
    // Defina os dados que você deseja enviar no corpo da requisição
    usuario: 'usuario',
    senha: 'senha',
    cpf_cnpj: 'cpf_cnpj',
    cep: 'cep',
    numero: 'telefone',
    email: 'email',
    foto: 'link_foto',
  });

  const handleLogin = () => {
    navigate('LoginScreen')
  };

  const handleCadastro = () => {
    // Lógica de cadastro aqui
    console.log('Cadastro pressionado');
    navigate('LoginScreen')
  };

  const enviarRequisicao = async () => {
    try {
      const resposta = await axios.post('http://127.0.0.1:8000/api/v1/cliente/', dados);

      // Lidar com a resposta da API aqui
      console.log('Resposta da API:', resposta.data);
    } catch (erro) {
      // Lidar com erros aqui
      console.error('Erro ao enviar requisição:', erro);
    }
  };

  return (
    <View style={styles.all}>
      <View style={styles.container}>
        
        <TextInput
          style={styles.input}
          placeholder="Usuário"
          value={dados.usuario}
          onChangeText={(texto) => setDados({ ...dados, chave2: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="Senha"
          value={dados.senha}
          onChangeText={(texto) => setDados({ ...dados, chave2: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="CPF/CNPJ"
          value={dados.cpf_cnpj}
          onChangeText={(texto) => setDados({ ...dados, chave3: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="CEP"
          value={dados.cep}
          onChangeText={(texto) => setDados({ ...dados, chave4: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="Telefone"
          value={dados.numero}
          onChangeText={(texto) => setDados({ ...dados, chave5: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="Email"
          value={dados.email}
          onChangeText={(texto) => setDados({ ...dados, chave6: texto })}
        />
        <TextInput
          style={styles.input}
          placeholder="Foto"
          value={dados.foto}
          onChangeText={(texto) => setDados({ ...dados, chave7: texto })}
        />
        <Button title="Cadastrar!" onPress={handleLogin} />

        <TouchableOpacity onPress={enviarRequisicao}>
        <Text></Text>
        <Text style={styles.cadastroButton}>Ir Para tela de Login</Text>
      </TouchableOpacity>

      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  all: {
    flex: 1
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  input: {
    height: 40,
    width: '80%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 16,
    padding: 8,
  },
  cadastroButton: {
    color: 'blue',
    marginTop: 16,
  },
});

export default Cadastro;
