import React from 'react';
import { View, Text, TouchableOpacity, Dimensions, StyleSheet } from 'react-native';

const { width, height } = Dimensions.get('window');

const Header = ({ userName }) => {
  return (
    <View style={styles.header}>
      <TouchableOpacity style={styles.button}>
        {/* Conteúdo do botão */}
      </TouchableOpacity>
      <Text style={styles.welcomeText}>Seja bem-vindo, {userName}!</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between', // Isso ajuda a ajustar o botão e o texto
    backgroundColor: 'blue', // Cor de fundo do cabeçalho
    height: 60, // Altura do cabeçalho
  },
  button: {
    width: width * 0.1, // 10% da largura da tela
    height: width * 0.1, // Mesma altura para manter o botão redondo
    borderRadius: width * 0.05, // Raio para torná-lo redondo
    backgroundColor: 'red', // Cor de fundo do botão redondo
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft: 10, // Espaçamento à esquerda
  },
  welcomeText: {
    flex: 1, // Ocupará o restante do espaço disponível
    fontSize: 18,
    color: 'white', // Cor do texto
    marginLeft: 10, // Espaçamento à esquerda entre o botão e o texto
    justifyContent: left,
  },
});

export default Header;
