import Voltar from "../../components/Voltar";
import { StyleSheet, Text, View, FlatList } from 'react-native';
import Movements from "../../components/Movements";
import axios from 'axios'
import React from 'react';



const User = () => {

  const [usuario, setSaldo] = useState("");

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(
          "http://10.109.71.22:8000/api/v1/cliente/2/",
          {
            headers: {
              Authorization: "Token 63d15c6d3adeb0eff6f27a2acaa9bc025f976c11",
            },
          }
        );
        setSaldo(response.data.saldo);
        console.log(response.data)
      } catch (error) {
        console.error("Erro ao obter usuário:", error);
      }
    }

    fetchData();
  }, []);
  
  return (
    <View style={styles.all}> 
    <Voltar text='Usuer'/>
        <View style={styles.container}>
        <View style={styles.circle} />

      <Text style={styles.label}>USUÁRIO</Text>
      <Text style={styles.value}>NomeDeUsuario</Text>
      <Text> </Text>

      <Text style={styles.label}>CPF_CNPJ</Text>
      <Text style={styles.value}>123.456.789-00</Text>
      <Text> </Text>

      <Text style={styles.label}>CEP</Text>
      <Text style={styles.value}>12345-678</Text>
      <Text> </Text>

      <Text style={styles.label}>TELEFONE</Text>
      <Text style={styles.value}>(12) 3456-7890</Text>
      <Text> </Text>

      <Text style={styles.label}>EMAIL</Text>
      <Text style={styles.value}>usuario@example.com</Text>
        
        </View>
    </View>
  );
};

const styles = StyleSheet.create({
    all: {
        flex: 1,
    },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  circle: {
    width: 200,
    height: 200,
    borderRadius: 100,
    backgroundColor: 'lightblue',
    marginBottom: 20,
  },
  fieldContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  label: {
    fontWeight: 'bold',
    marginRight: 10,
  },
  value: {
    flexShrink: 1,
  },
});

export default User;
