import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

const Header = ({ userName }) => {
  return (
    <View style={styles.header}>
      <TouchableOpacity style={styles.button}>
        {/* Conteúdo do botão redondo (ícone, texto, etc.) */}
      </TouchableOpacity>
      <Text style={styles.welcomeText}>Seja bem-vindo, {userName}!</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'blue', // Cor de fundo do cabeçalho
    height: 60, // Altura do cabeçalho
  },
  button: {
    width: 40, // Largura do botão redondo
    height: 40, // Altura do botão redondo
    borderRadius: 20, // Raio para torná-lo redondo
    backgroundColor: 'red', // Cor de fundo do botão redondo
    justifyContent: 'center',
    alignItems: 'center',
    
     // Espaçamento à esquerda
  },
  welcomeText: {
    fontSize: 18,
    color: 'white', // Cor do texto
    marginLeft: 60, // Espaçamento à esquerda entre o botão e o texto
    
  },
});

export default Header;
