import Voltar from "../../components/Voltar";
import { StyleSheet, Text, View, FlatList, TouchableOpacity, TextInput} from 'react-native';
import Movements from "../../components/Movements";
import axios from 'axios'
import React, { useState, useEffect } from 'react';

const list = [
    {
        id: 1,
        label: "Boleto conta de luz",
        value: "300,90",
        date: '17/09/2022',
        type: 0
    },
    {
        id: 2,
        label: 'pix Cliente x',
        value: '2.500,00',
        date: '22/09/2022',
        type: 1,
    },
    {
        id: 3,
        label: 'salario',
        value: '7.200,00',
        date: '30/09/2022',
        type: 1,
    },
    {
        id: 4,
        label: 'salario',
        value: '7.200,00',
        date: '30/09/2022',
        type: 1,
    },
]

export default function User(){

    const [data, setData] = useState("")
    const [valor, setValor] = useState("");

    useEffect(() => {
        async function fetchData() {
          try {
            const response = await axios.get(
              "http://10.109.71.22:8000/api/v1/cliente/2/movimentacao/",
              {
                headers: {
                  Authorization: "Token 63d15c6d3adeb0eff6f27a2acaa9bc025f976c11",
                },
              }
            );
            setData(response.data)
            setValor(response.data.valor)
            console.log(response.data)
          } catch (error) {
            console.error("Erro ao obter usuário:", error);
          }
        }
    
        fetchData();
      }, []);

    return(
        <View style={styles.container}>
            <Voltar text="Movimentações"/>
        
                <View style={styles.quadrado}>
                    <View style={styles.square}>

                    <TextInput
                    style={styles.input}
                    placeholder="Conta"
                    //onChangeText={(text) => setNumero(text)}
                    />

                    <TextInput
                    style={styles.input}
                    placeholder="Valor"
                    //onChangeText={(text) => setNumero(text)}
                    />


                        <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Fazer Transação</Text>
                 </TouchableOpacity>
             </View>
        </View>


            <FlatList
            style={styles.list}
            data={data.valor}
            keyExtractor={(item) => String(item.codigo)}
            showsVerticalScrollIndicator={false}
            renderItem={ ({item}) =>  

            

            <View style={styles.container}>
                <View style={styles.content}>

                <Text >R$ {data.valor}</Text>
                </View>
        </View>

        }
            />
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#EAFDFF",
        justifyContent: 'center',

    },
    quadrado: {
        flex: 1,
        backgroundColor: "#EAFDFF",
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    square: {
        width: 200,
        height: 200,
        backgroundColor: 'lightgray',
        justifyContent: 'center',
        alignItems: 'center',
      },
      label: {
        fontSize: 18,
        marginVertical: 5,
      },
      button: {
        backgroundColor: 'blue',
        padding: 10,
        marginTop: 10,
        borderRadius: 5,
      },
      buttonText: {
        color: 'white',
        fontSize: 16,
        textAlign: 'center',
      },
    });
